class ddomr{async init(path,doc){let params=new URLSearchParams(document.location.search.substring(1)),routing=params.get("routing");routing&&(path=routing);const html=await(await fetch(path)).text();window.history.pushState({html:html,pageTitle:"DDOMR Page"},"",path),doc.querySelector("html").innerHTML=html,Array.from(doc.querySelectorAll("script.ddomr-script")).forEach(async function(element){element.hasAttribute("src")?eval(await(await fetch(element.src)).text()):eval(element.innerHTML)})}async change(path,doc){const html=await(await fetch(path)).text();window.history.pushState({html:html},"",path),doc.querySelector("html").innerHTML=html,Array.from(doc.querySelectorAll("script.ddomr-script")).forEach(async function(element){element.hasAttribute("src")?eval(await(await fetch(element.src)).text()):eval(element.innerHTML)})}}